use crate::export;
use crate::util_internal::account_helper::gen_extra_data;
use crate::util_internal::chain::map_coin_type;
use anyhow::format_err;
use anyhow::Error;
use hex;
use serde::{Deserialize, Serialize};
use serde_json::json;
use ur_registry::crypto_hd_key::CryptoHDKey;
use ur_registry::registry_types::CRYPTO_HDKEY;
use ur_registry::traits::From;

#[derive(Default, Clone, Debug, Serialize, Deserialize)]
pub struct OkxExtra {
    pub chain_id: u32,
}

#[derive(Default, Clone, Debug, Serialize, Deserialize)]
pub struct AccountExtra {
    pub okx: OkxExtra,
}

#[derive(Default, Clone, Debug, Serialize, Deserialize)]
pub struct Account {
    chain: String,
    path: String,
    public_key: String,
    name: String,
    chain_code: String,
    extended_public_key: String,
    #[serde(skip_serializing_if = "Option::is_none")]
    note: Option<String>,
    #[serde(skip_serializing_if = "Option::is_none")]
    xfp: Option<String>,
    extra: AccountExtra,
}

impl core::convert::From<&CryptoHDKey> for Account {
    fn from(value: &CryptoHDKey) -> Account {
        let hd_path = value
            .get_origin()
            .unwrap_or_default()
            .get_path()
            .unwrap_or_default();
        let coin_type = value
            .get_origin()
            .unwrap_or_default()
            .get_components()
            .to_vec()[1]
            .get_index()
            .unwrap_or_default();
        let chain_code = hex::encode(value.get_chain_code().unwrap_or_default());
        let mut xpub = "".to_string();
        if !chain_code.is_empty()
            && value.get_parent_fingerprint().is_some()
            && value.get_origin().is_some()
        {
            xpub = value.get_bip32_key();
        }

        let source_fingerprint = value
            .get_origin()
            .unwrap_or_default()
            .get_source_fingerprint();
        let xfp = if source_fingerprint.is_some() {
            Some(hex::encode(source_fingerprint.unwrap()))
        } else {
            None
        };

        Account {
            chain: map_coin_type(coin_type),
            path: format!("m/{}", hd_path),
            public_key: hex::encode(value.get_key()),
            name: value.get_name().unwrap_or_default(),
            chain_code,
            extended_public_key: xpub,
            note: value.get_note(),
            xfp,
            extra: gen_extra_data(coin_type),
        }
    }
}

export! {
    @Java_com_keystone_sdk_KeystoneNativeSDK_parseCryptoHDKey
    fn parse_crypto_hd_key(ur_type: &str, cbor_hex: &str) -> String {
        if CRYPTO_HDKEY.get_type() != ur_type {
            return json!({"error": "type not match"}).to_string();
        }

        let parse_signature = || -> Result<Account, Error> {
            let cbor = hex::decode(cbor_hex.to_string())?;
            let crypto_hd_key = CryptoHDKey::from_cbor(cbor).map_err(|_| format_err!(""))?;
            Ok(Account::from(&crypto_hd_key))
        };
        match parse_signature() {
            Ok(hd_key) => json!(hd_key).to_string(),
            Err(_) => json!({"error": "crypto hd key is invalid"}).to_string(),
        }
    }
}

#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_parse_crypto_hd_key() {
        let hd_key_cbor = "";
        let expect_result = r#"{"chain":"ETH","chain_code":"712a9187e5c60c573a5acce855445376e1b74c240e417fe8cb2a8fdfd78d2d9d","extended_public_key":"xpub6CBZfsQuZgVnvTcScAAXSxtX5jdMHtX5LdRuygnTScMBbKyjsxznd8XMEqDntdY1jigmjunwRwHsQs3xusYQBVFbvLdN4YLzH8caLSSiAoV","extra":{"okx":{"chain_id":1}},"name":"Keystone","note":"account.standard","path":"m/44'/60'/0'","public_key":"02cc6d7834204653ff10e0047a2395343cc6df081e76c88d5eee83f346f0b21cb7","xfp":"f23f9fd2"}"#;

        assert_eq!(
            expect_result,
            parse_crypto_hd_key("crypto-hdkey", hd_key_cbor)
        );
    }

    #[test]
    fn test_parse_crypto_hd_key_wrong_type() {
        let hd_keys_cbor = "";
        let expect_result = "{\"error\":\"type not match\"}";

        assert_eq!(
            expect_result,
            parse_crypto_hd_key("crypto-account", hd_keys_cbor)
        );
    }
}
